import { useState } from 'react';
import { uploadFileApi } from '../../apis/Files';
import Nav from '../Home/Nav';

const Files = () => {

    //Message
    const [fileMsg, setFileMsg] = useState("")
    const [apiMsg, setApiMsg] = useState("");

    //value
    const [file, setFile] = useState([]);
    const [allowedMimeTypes, setAllowedMimeTypes] = useState([
        "image/bmp",
        "image/gif",
        "image/jpeg",
        "image/png",
        "image/svg+xml",
        "image/webm"
    ]);
    const [imagePath, setImagePath] = useState("");

    const uploadFile = () => {
        let noOfErrors = 0;
        let fileTypeError = 1;
        console.log(file);
        if (file.length == 0) {
            noOfErrors = 1;
            setFileMsg("Please select image");
        }
        if (file.length > 0) {
            allowedMimeTypes.map((type) => {
                if (type == file[0].type) {
                    fileTypeError = 0;
                }
            });
            if (fileTypeError == 1) {
                noOfErrors = 1;
                setFileMsg("Images only allowed");
            }
            else {
                //1 kb = 1024 bytes
                //1 mb = 1024 KB
                //2 MB = 2 * 1024 * 1024 bytes
                // setFileMsg("");
                if (file[0].size > 2097152) {
                    noOfErrors = 1;
                    setFileMsg("Max allowed size is 2 MB");
                }
                else {
                    setFileMsg("");
                }
            }
        }
        if (noOfErrors == 0) {
            uploadFileApi("react app", file[0]).then(
                (response) => {
                    // console.log(response);
                    if (response.data.result === "success") {
                        setApiMsg(response.data.data.imagePath);
                        setImagePath(response.data.data.imagePath);
                    }
                    else {
                        setApiMsg(response.data.msg);
                    }
                }
            )
        }
    };

    return (
        <>
            <div className="container fixed-top-padding">
                <Nav />
                <div className="row">
                    <div className="col-lg-2"></div>
                    <div className="col-lg-7">
                        <div className="card shadow border-0">
                            <div className="card-body">
                                <h3>File uploads</h3>
                                <div className="row">
                                    <div className="col-lg-12 mt-4">
                                        <label>SELECT FILE</label>
                                        <div className="custom-file">
                                            <input
                                                type="file"
                                                className="custom-file-input"
                                                onChange={(e) => setFile(e.target.files)}
                                            />
                                            <label className="custom-file-label" htmlFor="imputGroupFile2">
                                                Choose file
                                        </label>
                                        </div>
                                        <span className="text-danger">{fileMsg}</span>
                                    </div>
                                    <div className="col-lg-12 mt-4 mb-3">
                                        <button
                                            type="button"
                                            clas sName="btn btn-primary"
                                            onClick={uploadFile}>Upload File</button>

                                    </div>
                                    <div className="col-lg-12 mt-4">
                                        <p>{apiMsg}</p>
                                    </div>
                                    <div className="col-lg-12 mt-4">
                                        {imagePath.length > 0 && 
                                        <img src={imagePath} alt=""
                                    className="img-fluid"/>
                                }
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* pdf or document uploader */}
                <div className="row mt-4">
                <div className="col-lg-2"></div>
                    <div className="col-lg-7">
                        <div className="card shadow border-0">
                            <div className="card-body">
                                <h3>Upload Resume</h3>
                                <div className="row">
                                    <div className="col-lg-12 mt-4">
                                        <label>SELECT FILE</label>
                                        <div className="custom-file">
                                            <input
                                                type="file"
                                                className="custom-file-input"
                                                onChange={(e) => setFile(e.target.files)}
                                            />
                                            <label className="custom-file-label" htmlFor="imputGroupFile2">
                                                Choose file
                                        </label>
                                        </div>
                                        <span className="text-danger">{fileMsg}</span>
                                    </div>
                                    <div className="col-lg-12 mt-4 mb-3">
                                        <button
                                            type="button"
                                            className="btn btn-primary"
                                            onClick={uploadFile}>Upload File</button>

                                    </div>
                                    <div className="col-lg-12 mt-4">
                                        <p>{apiMsg}</p>
                                    </div>
                                    <div className="col-lg-12 mt-4">
                                        {imagePath.length > 0 && 
                                        <img src={imagePath} alt=""
                                    className="img-fluid"/>
                                }
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>
            </div>
        </>
    )
}

export default Files;
