import { useState } from "react";
import Nav from "../Home/Nav"

const Data = () => {

    const [name, setName] = useState("");
    const [nameMsg, setNameMsg] = useState("");

    const [addCard, setAddCard] = useState(
        localStorage.getItem("addCard") == null ? [] :
            JSON.parse(localStorage.getItem("addCard"))
    );


    const addData = () => {

        if (name.length < 4) {
            setNameMsg("add min 4 char")
        }
        else {
            setNameMsg("");
            
            let newCard = {
                name: name,
                id:addCard.length,
            }

            // setAddCard([...addCard, newCard]);
            setAddCard([...addCard])
            localStorage.setItem("addCard", JSON.stringify([...addCard, newCard]))

        };

    }

    const deleteFriend = (e) => {
        console.log("delete clicked");

        var index = e.target.getAttribute("index");
        var newData = addCard.filter((friend, i) => {
            return index != i;
        });
        console.log(newData);
        setAddCard([...newData]);
        localStorage.setItem("addCard", JSON.stringify(newData));
    };

    return (
        <>
            <div className="container fixed-top-padding">
                <Nav />
                <div className="row">
                    <div className="col-lg-12">
                        <input type="text" placeholder="name" value={name} onChange={(e) => setName(e.target.value)} />
                        <small className="text-danger">{nameMsg}</small>
                    </div>
                    <div className="col-lg-12 mt-3">
                        <button type="button" className="btn btn-primary" onClick={addData}>Click</button>
                    </div>
                </div>


                <div className="row">
                    {addCard.map((card, i) => (
                        <div className="col-lg-12 mt-4" key={i}>
                            <div className=" border border-1 border border-success rounded-lg">
                                <div className="card-data">
                                    <div className="row">
                                        <div className="col-lg-9">
                                            {card.name}

                                        </div>
                                        <div className="col-lg-3 text-right  ">
                                            <span
                                                index={i}
                                                className='fb-pointer'
                                                onClick={deleteFriend}
                                            >
                                                Delete
                                            </span>
                                        </div>
                                    </div>
                                </div>
                            </div> <br />
                        </div>
                    ))}
                </div>
            </div>
        </>
    )
}

export default Data;
