import { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';

const DragAndDrop = () => {

    const [allowedMimeTypes, setAllowedMimeTypes] = useState([
        "image/bmp",
        "image/gif",
        "image/jpeg",
        "image/png",
        "image/svg+xml",
        "image/webm"
    ]);

    const [noOfValideFiles,setNumberOfValidFiles] = useState(0);


    const onDrop = useCallback((files) => {
        let validFilesCount
        files.map((file) =>{
            // console.log(file);
            
            let typeMatching = 0;
            allowedMimeTypes.map( type => {
                if(type == file.type){
                    typeMatching = 1;
                }
            });
            if( typeMatching == 1) {
                if( file.size < 2097152){
                    setNumberOfValidFiles(noOfValideFiles + 1 );
                    console.log("call api");
                }
            }
            
        });
     
    }, []);
    const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop });


return (
    <>
        <div className="row mt-4 mb-4">
            <div className="col-lg-2">  </div>
            <div className="col-lg-7">
                <div className="card shadow border-0">
                    <div className="card-body" {...getRootProps()}>
                        <input {...getInputProps()} />
                Drag and Drop files here
                </div>
                </div>
            </div>

        </div>
    </>
)
    }

export default DragAndDrop;
