import { useState } from "react";
import Nav from "../Home/Nav"

const Data = () => {

                const [name,setName]= useState("");
                const [nameMsg,setNameMsg] = useState("");  

                const[addCard,setAddCard] = useState(
                    localStorage.getItem("addCard") == null ? []:
                    JSON.parse(localStorage.getItem("addcard"))
                );


                const addData = () =>{

                    if(name.length < 4) {
                        setNameMsg("add min 4 char")
                    }
                    else{
                        setNameMsg("");
                        let newCard = {
                            name:name
                        }
                        setAddCard.push([...newCard]);
                        localStorage.setItem("addCard",JSON.stringify(addCard))

                    };

                }
    return(
        <>
        <div className="container fixed-top-padding">
            <Nav/>
            <div className="row">
                <div className="col-lg-12">
                    <input type="text" placeholder="name" value={name} onChange={e=>SVGAnimateTransformElement(e.target.value)}/>
              <small class="text-danger">{nameMsg}</small>
                </div>
                <div className="col-lg-12">
                <button type="button" className="btn btn-primary" onClick={addData}>Click</button>
            </div>
            </div>
        </div>
        </>
    )
}

export default Data;
