const Comments = ({comment}) =>{
    return (
        <>
           <div className="media">
               <img src={comment.user_pic} alt="pic"  className="link-icon link-radius"/>
               <div className="media-body">
                   <strong>{comment.user_name}</strong>
                   
               </div>
           </div>
        </>
    )
}

export default Comments;
